import React, { useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import SingleDBJob from './SingleData'

function SingleJob() {
    let {id} = useParams()
  return (
    <div className='container-lg container-fluid p-md-5'><BringSingleJob data={{id,SingleDBJob}}/></div>
  )
}




function BringSingleJob({data}){
    console.log(data.id )
    console.log(data.SingleDBJob.id )
    const navigate = useNavigate()
    useEffect(()=>{
        if(data.id != data.SingleDBJob.id) return navigate("/");
    },[])
    return(<>
        <Link to='/' className='d-block text-start h5 link-underline link-underline-opacity-0 text-danger'><i className="bi bi-arrow-left"></i> &nbsp; See All Jobs</Link>
        <div className="container-fluid bg-white p-5">
            <div className="head">
                <img src={`./assets/1.jpg`} alt="" />
                <h2>Regional Mechanical Supervisor</h2>
                <p>Afghan Wireless Communication Company, Takhar, Afghanistan </p>
                <div>
                    <button className="btn"></button>
                    <button className="btn">Apply Now</button>
                </div>
            </div>
        </div>
    </>)
}






export default SingleJob
