import React, { useRef, useEffect } from 'react';
import Swiper from 'swiper';
import 'swiper/css';

const SwiperCards = () => {
  const swiperRef = useRef(null);

  useEffect(() => {
    const swiper = new Swiper(swiperRef.current, {
      loop: true,
      slidesPerView: 'auto', // Set to 'auto' to fit as many cards as possible in one slide
      spaceBetween: 20, // Adjust space between cards
      centeredSlides: true, // Center the active slide
      autoplay: {
        delay: 3000,
        disableOnInteraction: false,
      },
    });

    return () => {
      swiper.destroy();
    };
  }, []);

  return (
    <div ref={swiperRef} className="swiper-container">
      <div className="swiper-wrapper">
        {/* Slide 1 */}
        <div className="swiper-slide">
          <div className="card">
            {/* Card content */}
          </div>
          <div className="card">
            {/* Card content */}
          </div>
          <div className="card">
            {/* Card content */}
          </div>
        </div>

        {/* Slide 2 */}
        <div className="swiper-slide">
          <div className="card">
            {/* Card content */}
          </div>
          <div className="card">
            {/* Card content */}
          </div>
          <div className="card">
            {/* Card content */}
          </div>
        </div>

        {/* Add more slides and cards as needed */}
      </div>
    </div>
  );
};

export default SwiperCards;
