import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";

import "./styles.css";

// import required modules
import { Autoplay, Navigation } from "swiper/modules";

export default function SwiperCards() {
  const [swiperKey, setSwiperKey] = useState(0); // Add a state for the key

  const handleRefresh = () => {
    setSwiperKey(prevKey => prevKey + 1); // Increment the key to force reinitialization
  };
  return (
    <>
    <div className="container-fluid px-2 px-md-3 px-lg-5 container container-md">
      <div className="swiper-wrapper">
        <Swiper
          navigation={true}
          modules={[Navigation, Autoplay]}
          autoplay={{ delay: 2500,pauseOnMouseEnter:true }}
          slidesPerView={1} // Set number of slides per view
          breakpoints={{
            // Set different configurations based on device size
            850: {
              slidesPerView: 2, // Show 2 slides per view on devices larger than or equal to 768px (tablets and desktops)
            },
            1200: {
              slidesPerView: 3, // Show 3 slides per view on devices larger than or equal to 1024px (desktops)
            },
          }}
          spaceBetween={30}
          className="mySwiper"
        >
          <SwiperSlide className="swiper-card bg-danger">Slide 1</SwiperSlide>
          <SwiperSlide className="swiper-card bg-primary">Slide 2</SwiperSlide>
          <SwiperSlide className="swiper-card bg-secondary">Slide 3</SwiperSlide>
          <SwiperSlide className="swiper-card bg-success">Slide 4</SwiperSlide>
          <SwiperSlide className="swiper-card bg-warning">Slide 5</SwiperSlide>
          <SwiperSlide className="swiper-card bg-info">Slide 6</SwiperSlide>
          <SwiperSlide className="swiper-card bg-dark">Slide 7</SwiperSlide>
          <SwiperSlide className="swiper-card bg-info">Slide 8</SwiperSlide>
          <SwiperSlide className="swiper-card bg-white">Slide 9</SwiperSlide>
        </Swiper>
      </div>
    </div>
    </>
  );
}
